import React, { useEffect, useState } from "react";
import { Container, Table } from "reactstrap";
import Helmet from "../components/Helmet/Helmet";
import CommonSection from "../components/UI/CommonSection";
import DoctorItem from "../components/UI/DoctorItem";
import axios from "axios";
import { createUrl, log } from "../utils/utils";
import "../styles/header.css";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";

const DoctorListing = () => {

  const navigate = useNavigate();
  const [doctor, setDoctor] = useState([]);
  const [selectedDoctor, setSelectedDoctor] = useState("");
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    contactNo: "",
    relation: "",
  });

  const patientId = sessionStorage.getItem("userId");
  
  const days = [
    "Sunday",
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday"
  ];
  const [selectedDay, setSelectedDay] = useState("");

  // Event handler for when a day is selected
  const handleDayChange = (event) => {
    setSelectedDay(event.target.value);
  };
  useEffect(() => {
    loadDoctors();
  }, []);

  const loadDoctors = () => {
    const url = createUrl("/doctor");
    axios
      .get(url)
      .then(function (response) {
        setDoctor(response.data);
        setLoading(false); // Set loading to false after fetching data
        log(response);
      })
      .catch(function (error) {
        log(error);
        setLoading(false); // Set loading to false in case of error
      });
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const bookAppointment = async (event) => {
    

    event.preventDefault(); // Prevent default form submission behavior
    const url = createUrl("/appoinment");
    console.log(selectedDoctor);
    const Body = {
      ...formData,
      patientId: sessionStorage.getItem("userId"),
      doctorId:selectedDoctor
    };
    debugger;
    try {
      const response = await axios.post(url, Body);
      console.log("Appointment booked successfully:", response.data);
      toast.success("Appointment booked successfully");
      navigate('/PatientServices')
      
    } catch (error) {
      console.error("Error booking appointment:", error);
    }
  };

  return (
<><div>
      <Helmet title="Doctor">
        <CommonSection title="Appointment Form" />
        <section>
          <Container>
            <Table striped>
              <thead>
                <tr>
                  <th>Doctor Name</th>
                  <th>Contact Number</th>
                  <th>Specialization</th>
                  <th>Day</th>
                  <th>Time</th>
                </tr>
              </thead>
              <tbody>
                {doctor.map((doc, index) => (
                  <DoctorItem key={index} doctor={doc} />
                ))}
              </tbody>
            </Table>
          </Container>
        </section>
      </Helmet>

      <div>
        <hr />
      </div>
</div>
      {sessionStorage.getItem("userRoles")==="Patient" && (
        <>        <div className="container">
        <form onSubmit={bookAppointment}>
            <div className="table-responsive">
               <table className="table table-bordered">
                <thead>
                   <tr>
                     <th colSpan="2" className="text-center">
                       <strong>Fill Appointment Details</strong>
                     </th>
        </tr>
                </thead>
                   <tbody>
                 <tr>
                   <td>
                       <strong>Patient Name:</strong>
                     </td>
                     <td>
                       <input
                         type="text"
                         className="form-control"
                         name="name"
                         value={formData.name}
                         onChange={handleInputChange}
                         placeholder="Enter patient name"
                         required
                       />
                     </td>
                   </tr>
                  <tr>
                    <td>
                      <strong>Patient Age:</strong>
                     </td>
                     <td>
                       <input
                         type="text"
                       className="form-control"
                         name="age"
                         value={formData.age}
                         onChange={handleInputChange}
                         placeholder="Enter patient age"
                         required
                       />
                     </td>
                   </tr>
                   <tr>
                     <td>
                       <strong>Patient Contact No:</strong>
                     </td>
                     <td>
                       <input
                         type="text"
                         className="form-control"
                         name="contactNo"
                        value={formData.contactNo}
                        onChange={handleInputChange}
                        placeholder="Enter contact number"
                        required
                      />
                     </td>
                   </tr>
                   <tr>
                     <td>
                       <strong>Relation with the Patient:</strong>
                     </td>
                     <td>
                       <input
                         type="text"
                         className="form-control"
                         name="relation"
                         value={formData.relation}
                         onChange={handleInputChange}
                         placeholder="Enter relation"
                         required
                       />
                     </td>
                   </tr>
                     <td>
                       <div>
      <label htmlFor="days">Select a day:</label>
      <select id="days" value={selectedDay} onChange={handleDayChange}>
        <option value="">Choose a day</option>
        {days.map((day, index) => (
          <option key={index} value={day}>{day}</option>
        ))}
      </select>
      {selectedDay && <p>You selected: {selectedDay}</p>}
    </div>
                       </td>
                     
                   {loading ? ( // Show loading indicator if data is being fetched
                     <tr>
                       <td colSpan="2" className="text-center">
                         Loading...
                       </td>
                     </tr>
                   ) : doctor.length > 0 ? ( // Render select options if doctor data is available
                     <tr>
                       <td>
                         <strong>Select Doctor:</strong>
                       </td>
                       <td>
                         <select
                           className="form-select select-doctor"
                           aria-label="Select doctor"
                           onChange={(e) => setSelectedDoctor(e.target.value)}
                           required
                         >
                           <option value="">Select a doctor</option>
                           {doctor.map((doc, index) => (
                             <option key={index} value={doc.id}>
                               {doc.dname}
                             </option>

                             
                           ))}
                         </select>
                       </td>
                       
                     </tr>

                   ) : (
                     <tr>
                       <td colSpan="2" className="text-center">
                         No doctors available
                       </td>
                     </tr>
                   )}
                 </tbody>
               </table>
             </div>
             <div className="text-center">
               <button type="submit" className="btn btn-primary">
                 Submit
             </button>
           </div>
           </form>
         </div>
       
     </>
      )}
        </>

  );
};

export default DoctorListing;

