import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { getPatientById } from "../../services/user";
import axios from 'axios';

const EditPatient = () => {
    const [patient, setPatient] = useState({});
    const navigate = useNavigate();

    useEffect(() => {
        getPatient();
    }, []);

    const getPatient = async () => {
        try {
            const patientId = sessionStorage.getItem("userId");
            const response = await getPatientById(patientId);
            setPatient(response);
        } catch (error) {
            console.error(error);
            toast.error("An error occurred during fetching patient");
        }
    };

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setPatient({ ...patient, [name]: value });
    };

    const updateRecord = () => {
        const patientId = sessionStorage.getItem("userId");
        const updatedUrl = `/patient/patientId/${patientId}`;

        axios.put(updatedUrl, patient)
            .then((result) => {
                if (result.data !== undefined && result.data > 0) {
                    toast.success("Profile Updated Successfully");
                } else {
                    toast.error("An error occurred during getting response");
                }
            })
            .catch((error) => {
                console.error("Error updating record:", error);
                toast.error("An error occurred during updating profile");
            });
    };

    const handleBack = () => {
        navigate('/patient_profile');
    };

    return (
        <div className="container">
            <hr />
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <div className="card">
                        <div className="card-header">
                            <h2 className="card-title">{patient.name}  edit your profile here</h2>
                        </div>
                        <div className="card-body">
                            <form>
                                <div className="mb-3">
                                    <label htmlFor="name" className="form-label">Name</label>
                                    <input type="text" className="form-control" id="name" name="name" value={patient.name || ''} onChange={handleInputChange} />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="email" className="form-label">Email</label>
                                    <input type="email" className="form-control" id="email" name="email" value={patient.email || ''} onChange={handleInputChange} />
                                </div>

                                <div className="mb-3">
                                    <label htmlFor="password" className="form-label">Password</label>
                                    <input type="password" className="form-control" id="password" name="password" value={patient.password || ''} onChange={handleInputChange} />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="contactNo" className="form-label">Contact No</label>
                                    <input type="text" className="form-control" id="contactNo" name="contactNo" value={patient.contactNo || ''} onChange={handleInputChange} />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="age" className="form-label">Age</label>
                                    <input type="text" className="form-control" id="age" name="age" value={patient.age || ''} onChange={handleInputChange} />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="address" className="form-label">Address</label>
                                    <input type="text" className="form-control" id="address" name="address" value={patient.address || ''} onChange={handleInputChange} />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="bloodGroup" className="form-label">Blood Group</label>
                                    <input type="text" className="form-control" id="bloodGroup" name="bloodGroup" value={patient.bloodGroup || ''} onChange={handleInputChange} />
                                </div>
                                <div className="mb-3">
                                    <button type="button" className="btn btn-success me-2" onClick={updateRecord}>Update</button>
                                    <button type="button" className="btn btn-primary" onClick={handleBack}>Back</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default EditPatient;


