import React from "react";
import { Col } from "reactstrap";
import { Link, useNavigate } from "react-router-dom";
import "../../styles/car-item.css";
import { createUrl } from "../../utils/utils";

const AppointmentItem = ({ appointment }) => {

  const [appointmentStatus, setAppointmentStatus] = useState(true);

  const handleStatusChange = () => {
    // Assuming you have a function to handle the PUT request to update the status in the database
    // Replace `updateStatusInDatabase` with the actual function name
   // updateStatusInDatabase(!appointmentStatus); // Inverting the current status
    setAppointmentStatus(!appointmentStatus); // Update the local state
  };
  return (
    <tr>
      <td>{appointment.name}</td>

      <td>{appointment.age}</td>
      <td>{appointment.relation}</td>

       <td>
        <ul>
          {appointment.doctor.dschedule.map((schedule, index) => (
            <li key={index}>{schedule.day}</li>
          ))}
        </ul>
      </td> 
      <td>
        <ul>
          {appointment.doctor.dschedule.map((schedule, index) => (
            <li key={index}>{schedule.time}</li>
          ))}
        </ul>
      </td> 
      <td>{appointment.paymentAmount}</td>
      <td>{appointment.timestamp}</td>
      <td>
              <button
                type="button"
                className="btn btn-primary"
                onClick={handleStatusChange}
              >
                Toggle Status
              </button>
            </td>
      <td>{appointment.paymentStatus}</td>

    </tr>
  );
};
export default AppointmentItem;



