import React, { useEffect, useState } from "react";
import { Container, Row, Col, Button } from "reactstrap"; // Importing Bootstrap components
import HeroSlider from "../components/UI/HeroSlider";
import Helmet from "../components/Helmet/Helmet";
import AboutSection from "../components/UI/AboutSection";
import DoctorItem from "../components/UI/DoctorItem"; // Renamed from CarItem
import axios from "axios";
import { createUrl, log } from '../utils/utils';

const Home = () => {
  const [doctors, setDoctors] = useState([]); // Renamed from cars

  useEffect(() => {
    loadDoctors();
  }, []);

  const loadDoctors = () => {
    const url = createUrl('/doctors'); // Updated URL endpoint
    axios.get(url)
      .then(function (response) {
        setDoctors(response.data);
        log(response);
      })
      .catch(function (error) {
        log(error);
      })
      .finally(function () {
      });
  }

  return (
    <Helmet title="Home">
      <section className="p-0 hero__slider-section">
        <HeroSlider />
      </section>
      <AboutSection />
      <section className="py-5">
        <Container>
          <Row>
            <Col lg="12" className="text-center mb-5">
              <h6 className="section__subtitle">Meet Our Doctors</h6>
              <h2 className="section__title">Experienced Professionals</h2>
            </Col>
            {doctors.slice(0, 6).map((doctor) => (
              <DoctorItem doctor={doctor} key={doctor.id} /> // Updated from item to doctor
            ))}
          </Row>
          <Row className="justify-content-center mt-5">
            <Col xs="auto">
              <Button color="primary" size="lg" href="/doctors">View All Doctors</Button> {/* Added a button to view all doctors */}
            </Col>
          </Row>
        </Container>
      </section>
    </Helmet>
  );
};

export default Home;

