import React, { useState } from 'react';
import '../styles/finance.css';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';

const BmiCalculator = () => {
  const [height, setHeight] = useState(170);
  const [weight, setWeight] = useState(70);
  const [bmi, setBmi] = useState(0);

  const calculateBmi = () => {
    const heightInMeters = height / 100;
    const bmiValue = weight / (heightInMeters * heightInMeters);
    setBmi(bmiValue.toFixed(2));
  };

  return (
    <Container>
      <Row className="justify-content-center mt-5">
        <Col md={6}>
          <div className="finance-container">
            <h1 className='f_h1 text-center mb-4'>BMI Calculator</h1>
            <Form>
              <Form.Group as={Row} className="mb-3">
                <Form.Label column sm="6">Height (cm)</Form.Label>
                <Col sm="6">
                  <Form.Control
                    type="number"
                    min="100"
                    max="300"
                    value={height}
                    onChange={(e) => setHeight(e.target.value)}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} className="mb-3">
                <Form.Label column sm="6">Weight (kg)</Form.Label>
                <Col sm="6">
                  <Form.Control
                    type="number"
                    min="30"
                    max="300"
                    value={weight}
                    onChange={(e) => setWeight(e.target.value)}
                  />
                </Col>
              </Form.Group>

              <div className="text-center">
                <Button onClick={calculateBmi} className="custom-button">Calculate BMI</Button>
              </div>
            </Form>

            <div className="result text-center mt-4">
              {bmi > 0 && <p>Your BMI: {bmi}</p>}
            </div>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default BmiCalculator;
